package uz.coder.daggerhilt.adaptersimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport uz.coder.daggerhilt.ui.news.OtherNewsFragmentimport uz.coder.daggerhilt.ui.news.SavedNewsFragmentclass NewsViewPager(    fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount(): Int {        return 2    }    override fun createFragment(position: Int): Fragment {        return when (position) {            0 -> OtherNewsFragment()            else -> SavedNewsFragment()        }    }}