package uz.coder.daggerhilt.adaptersimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.BaseAdapterimport uz.coder.daggerhilt.databinding.SpinnerItemBindingclass SpinnerAdapter(var list: ArrayList<String>) :    BaseAdapter() {    override fun getCount(): Int {        return list.size    }    override fun getItem(position: Int): String {        return list[position]    }    override fun getItemId(position: Int): Long {        return position.toLong()    }    override fun getView(position: Int, convertView: View?, parent: ViewGroup): View {        val spinnerItemBinding: SpinnerItemBinding = if (convertView == null) {            SpinnerItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        } else {            SpinnerItemBinding.bind(convertView)        }        spinnerItemBinding.currencyText.text = list[position]        return spinnerItemBinding.root    }}