package uz.coder.daggerhilt.database.daoimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategy.REPLACEimport androidx.room.Queryimport uz.coder.daggerhilt.database.entity.NewsEntity@Daointerface NewsDao {    @Insert    suspend fun insertNews(entity: NewsEntity)    @Insert(onConflict = REPLACE)    suspend fun insertNews(entity: List<NewsEntity>)    @Query("select * from NewsEntity")    suspend fun getNews():List<NewsEntity>}