package uz.coder.daggerhilt.nerworkingimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Queryimport uz.coder.daggerhilt.models.Newsinterface ApiService {    @GET("everything")    suspend fun getUsers(        @Query("q") q:String = "All",        @Query("from") from:String = "2022-03-02",        @Query("sortBy") sortBy:String = "publishedAt",        @Query("apiKey") apiKey:String = "9062130b56ca4be4935e86b52df2b9f4",    ):Response<List<News>>}