package uz.coder.daggerhilt.repositoryimport kotlinx.coroutines.flow.flowimport uz.coder.daggerhilt.database.dao.UserDaoimport uz.coder.daggerhilt.database.entity.UserEntityimport uz.coder.daggerhilt.nerworking.ApiServiceimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass UserRepository @Inject constructor(    private val apiService: ApiService,    private val userDao: UserDao) {    suspend fun getUsers() = flow { emit(apiService.getUsers()) }    suspend fun insertUsers(list: List<UserEntity>) = userDao.insertUser(list)    suspend fun getDbUsers() = userDao.getUsers()}