package uz.coder.daggerhilt.repositoryimport kotlinx.coroutines.flow.flowimport uz.coder.daggerhilt.database.dao.NewsDaoimport uz.coder.daggerhilt.database.entity.NewsEntityimport uz.coder.daggerhilt.nerworking.ApiServiceimport javax.inject.Injectimport javax.inject.Singleton@Singletonclass UserRepository @Inject constructor(    private val apiService: ApiService,    private val userDao: NewsDao) {    suspend fun getUsers() = flow { emit(apiService.getUsers()) }    suspend fun insertUsers(list: List<NewsEntity>) = userDao.insertNews(list)    suspend fun getDbUsers() = userDao.getNews()}